import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./"; // Assuming you have a CSS file for styling

export default function MyFolliwngPost() {
    const { userId } = useParams();
    const [followersCount, setFollowersCount] = useState(0);

    const handleFlowerClick = (imageId) => {
        fetch(`http://localhost:5000/flower/${userId}/${imageId}`, {
            method: "PUT",
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(data => {
            setFollowersCount(data.followersCount);
        })
        .catch(err => console.log(err));
    }

    const externalImages = [
      "/img/elon.jpg",
      "/img/elon.jpg",
      "/img/elon.jpg",
      // Add more image links as needed
    ];

    return (
        <div className="home">
            <div>
                <h2>Followers: {followersCount}</h2>
            </div>
            {externalImages.map((image, index) => (
                <div key={index} className="image-container">
                    <div
                        className="flower-image"
                        style={{ backgroundImage: `url(${image})` }}
                    />
                    <button onClick={() => handleFlowerClick(index + 1)}>Follow Me</button>
                </div>
            ))}
        </div>
    );
}
