import React, { useState } from "react";

export default function MyFollowingPost() {
    const [followersCount, setFollowersCount] = useState(5);

    const handleFollowClick = () => {
        setFollowersCount(prevCount => prevCount + 1);
    }

    const externalImages = [
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        // Add more image links as needed
    ];

    return (
        <div className="home">
            <div>
                <h2>followers: {followersCount}</h2>
            </div>
            <center></center><table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Image</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {externalImages.map((image, index) => (
                        <tr key={index}>
                            <td>{`Follower ${index + 1}`}</td>
                            <td>
                                <img src={image} alt={`follower ${index + 1}`} style={{ height: '50px', width: '50px' }} />
                            </td>
                            <td>
                                <button onClick={handleFollowClick} style={{ background: 'red' }}>Follow Me</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
