import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Link } from "react-router-dom";

export default function MyFolliwngPost() {
    const { userId } = useParams();
    const [followersCount, setFollowersCount] = useState(0);

    const handleFlowerClick = (imageId) => {
        // Assuming you have an API endpoint to increment the flower count for a specific image
        fetch(`http://localhost:5000/flower/${userId}/${imageId}`, {
            method: "PUT",
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(data => {
            setFollowersCount(data.followersCount); // Update followers count
        })
        .catch(err => console.log(err));
    }

    const externalImages = [
        "../img/elon.jpg",
        "../img/elon.jpg",
        "../img/elon.jpg",

    ];

    return (
        <div className="home">
            <div>
                <h2>Followers: {followersCount}</h2>
            </div>
            {externalImages.map((image, index) => (
                <div key={index}>
                    <img src={image} alt={` Image ${index + 1}`} />
                    <button onClick={() => handleFlowerClick(index + 1)}>Follow Me</button>
                </div>
            ))}
            <Link to={`/followers/${userId}`}>Followers: {followersCount}</Link>
        </div>
    );
}
