// flowing.js

import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export default function MyFolliwngPost() {
    const { userId } = useParams(); // Assuming you have a userId parameter in your route
    const [followersCount, setFollowersCount] = useState(0);
    const [followingCount, setFollowingCount] = useState(0);

    useEffect(() => {
        // Fetch followers count
        fetch(`http://localhost:5000/followers/${userId}`, {
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(followers => setFollowersCount(followers.length))
        .catch(err => console.log(err));

        // Fetch following count
        fetch(`http://localhost:5000/following/${userId}`, {
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(following => setFollowingCount(following.length))
        .catch(err => console.log(err));

        // Rest of your code...
    }, [userId]);

    return (
        <div className="home">
            <div>
                <h2>Followers: {followersCount}</h2>
                <h2>Following: {followingCount}</h2>
            </div>
            <Link to={`/followers/${userId}`}>Followers: {followersCount}</Link>
<Link to={`/following/${userId}`}>Following: {followingCount}</Link>

        </div>
    );
}
