import React, { useState } from "react";

export default function MyFollowingPost() {
    const [followersCount, setFollowersCount] = useState(5);

    const handleFlowerClick = () => {
        setFollowersCount(prevCount => prevCount + 1);
    }

    const externalImages = [
        "/public/",
        "img/elon.jpg",
        "/img/elon.jpg",
        // Add more image links as needed
    ];

    return (
        <div className="home">
            <div>
                <h2>Followers: {followersCount}</h2>
            </div>
            {externalImages.map((image, index) => (
                <div key={index}>
                    <img src={image} alt={`flowers ${index + 1}`} style={{ height: '5px', width: '5px' }} />
                    <button onClick={handleFlowerClick} style={{ color: 'red' }}>Follow Me</button>
                </div>
            ))}
        </div>
    );
}
