import React, { useEffect, useState } from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";
import InstagramShareButton from './InstagramShareButton'; // Import the InstagramShareButton component

export default function Home() {
  var picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png";
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [comment, setComment] = useState("");
  const [show, setShow] = useState(false);
  const [item, setItem] = useState([]);

  // ... (Your existing code)

  const handleShareFile = (posts) => {
    const fileInput = document.createElement("input");
    fileInput.type = "file";
    fileInput.accept = "image/*"; // Specify the allowed file types (e.g., images)
    fileInput.addEventListener("change", (event) => {
      const file = event.target.files[0];
      if (file) {
        // You can now handle the selected file (e.g., upload it to a server)
        console.log("Selected file:", file);

        // Assuming you have a function to upload the file to your server
        // uploadFileToServer(file);

        // Then, generate the Instagram share URL using the uploaded file's URL
        const postUrl = `https://www.instagram.com/p/${posts._id}/`;
        const instagramShareUrl = `https://www.instagram.com/sharer/sharer.php?u=${postUrl}`;

        // Open the Instagram share URL in a new window
        window.open(instagramShareUrl, '_blank');
      }
    });
    fileInput.click();
  };

  return (
    <div className="home">
      {data.map((posts) => {
        return (
          <div className="card" key={posts._id}>
            {/* ... (Your existing code) */}
            <div className="add-comment">
              <span className="material-symbols-outlined">mood</span>
              <input
                type="text"
                placeholder="Add a comment"
                value={comment}
                onChange={(e) => setComment(e.target.value)}
              />
              <button
                className="comment"
                onClick={() => makeComment(comment, posts._id)}
              >
                Post
              </button>
              <span
                className="material-symbols-outlined"
                onClick={() => handleShareFile(posts)}
              >
                share
              </span>
              <InstagramShareButton postUrl={`https://www.instagram.com/p/${posts._id}/`} />
            </div>
          </div>
        );
      })}
      {show && (
        <div className="showComment">
          {/* ... (your existing code) */}
        </div>
      )}
    </div>
  );
}
