import React, { useEffect, useState } from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";

export default function Home() {
  var picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png";
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [comment, setComment] = useState("");
  const [show, setShow] = useState(false);
  const [item, setItem] = useState([]);

  // ... (Your existing code)

  const handleShare = (post) => {
    const shareText = `Check out this post on MyApp: ${post.body} - ${window.location.href}`;
    const subject = "Shared Post";

    const emailLink = `mailto:?subject=${encodeURIComponent(
      subject
    )}&body=${encodeURIComponent(shareText)}`;

    window.open(emailLink);

    // You can implement similar share functionality for other social media platforms.
    // For example, for WhatsApp:
    // const whatsappLink = `https://api.whatsapp.com/send?text=${encodeURIComponent(shareText)}`;
    // window.open(whatsappLink);
  };

  return (
    <div className="home">
      {data.map((posts) => {
        return (
          <div className="card" key={posts._id}>
            <div className="card-header">
              <div className="card-pic">
                <img
                  src={posts.postedBy.Photo ? posts.postedBy.Photo : picLink}
                  alt=""
                />
              </div>
              <h5>
                <Link to={`/profile/${posts.postedBy._id}`}>
                  {posts.postedBy.name}
                </Link>
              </h5>
            </div>
            <div className="card-image">
              <img src={posts.photo} alt="" />
            </div>
            <div className="card-content">
              {posts.likes.includes(
                JSON.parse(localStorage.getItem("user"))._id
              ) ? (
                <span
                  className="material-symbols-outlined material-symbols-outlined-red"
                  onClick={() => unlikePost(posts._id)}
                >
                  favorite
                </span>
              ) : (
                <span
                  className="material-symbols-outlined"
                  onClick={() => likePost(posts._id)}
                >
                  favorite
                </span>
              )}
              <p>{posts.likes.length} Likes</p>
              <p>{posts.body} </p>
              <p
                style={{ fontWeight: "bold", cursor: "pointer" }}
                onClick={() => toggleComment(posts)}
              >
                View all comments
              </p>
            </div>
            <div className="add-comment">
              <span className="material-symbols-outlined">mood</span>
              <input
                type="text"
                placeholder="Add a comment"
                value={comment}
                onChange={(e) => setComment(e.target.value)}
              />
              <button
                className="comment"
                onClick={() => makeComment(comment, posts._id)}
              >
                Post
              </button>
              <span
                className="material-symbols-outlined"
                onClick={() => handleShare(posts)}
              >
                share
              </span>
            </div>
          </div>
        );
      })}
      {show && (
        <div className="showComment">
          {/* ... (your existing code) */}
        </div>
      )}
    </div>
  );
}
