import React, { useState, useEffect } from "react";

export default function MyFollowingPost() {
    const [followersCount, setFollowersCount] = useState(7);

    const handleFollowClick = async (follower) => {
        try {
            const response = await fetch('http://localhost:5000/api/incrementFollowersCount', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ follower }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            setFollowersCount(data.followersCount);
        } catch (error) {
            console.error('Fetch error:', error);
        }
    }

    useEffect(() => {
        const fetchFollowersCount = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/getFollowersCount');
                const data = await response.json();
                setFollowersCount(data.followersCount);
            } catch (error) {
                console.error('Fetch error:', error);
            }
        };

        fetchFollowersCount();
    }, []);

    const externalImages = [
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
    ];

    return (
        <div className="home">
            <div>
                <h2>followings: {followersCount}</h2>
            </div>
            <center>
                <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Image</th>
                        <th>Follow me</th>
                    </tr>
                </thead>
                <tbody>
                    {externalImages.map((image, index) => (
                        <tr key={index}>
                            <td>{`Follower ${index + 8}`}</td>
                            <td>
                                <img src={image} alt={`follower ${index + 1}`} style={{ height: '50px', width: '50px' }} />
                            </td>
                            <td>
                                <button onClick={() => handleFollowClick(`Follower ${index + 8}`)} style={{ background: 'red' }}>Follow Me</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
                </table>
            </center>
        </div>
    );
}
