import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Link } from "react-router-dom";

export default function MyFolliwngPost() {
    const { userId } = useParams();
    const [followersCount, setFollowersCount] = useState(0);
    const [followingCount, setFollowingCount] = useState(0);

    const handleFlowerClick = () => {
        // Assuming you have an API endpoint to increment the flower count
        fetch(`http://localhost:5000/flower/${userId}`, {
            method: "PUT",
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(data => {
            setFollowersCount(data.followersCount); // Update followers count
        })
        .catch(err => console.log(err));
    }

    useEffect(() => {
        // Fetch followers count
        fetch(`http://localhost:5000/followers/${userId}`, {
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(followers => setFollowersCount(followers.length))
        .catch(err => console.log(err));

        // Fetch following count
        fetch(`http://localhost:5000/following/${userId}`, {
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        })
        .then(res => res.json())
        .then(following => setFollowingCount(following.length))
        .catch(err => console.log(err));

        // Rest of your code...
    }, [userId]);

    return (
        <div className="home">
            <div>
                <h2>Followers: {followersCount}</h2>
                <h2>Following: {followingCount}</h2>
            </div>
            <button onClick={handleFlowerClick}>Flower</button>
            <Link to={`/followers/${userId}`}>Followers: {followersCount}</Link>
            <Link to={`/following/${userId}`}>Following: {followingCount}</Link>
        </div>
    );
}
