import React, { useState, useEffect } from "react";

export default function MyFollowingPost() {
    const [followersCount, setFollowersCount] = useState(0);

    const handleFollowClick = async () => {
        const response = await fetch('/api/incrementFollowersCount', {
            method: 'POST',
        });

        if (response.ok) {
            setFollowersCount(prevCount => prevCount + 1);
        }
    }

    useEffect(() => {
        const fetchFollowersCount = async () => {
            const response = await fetch('/api/followersCount');
            const data = await response.json();
            setFollowersCount(data.followersCount);
        };

        fetchFollowersCount();
    }, []);

    const externalImages = [
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        "/img/elon.jpg",
        // Add more image links as needed
    ];

    return (
        <div className="home">
            <div>
                <h2>followings: {followersCount}</h2>
            </div>
            <center>
                <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Image</th>
                        <th>Follow me</th>
                    </tr>
                </thead>
                <tbody>
                    {externalImages.map((image, index) => (
                        <tr key={index}>
                            <td>{`Follower ${index + 8}`}</td>
                            <td>
                                <img src={image} alt={`follower ${index + 1}`} style={{ height: '50px', width: '50px' }} />
                            </td>
                            <td>
                                <button onClick={handleFollowClick} style={{ background: 'red' }}>Follow Me</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
                </table>
            </center>
        </div>
    );
}
