import React, { useState } from "react";

export default function MyFollowingPost() {
  const [followersCounts, setFollowersCounts] = useState(
    Array(externalImages.length).fill(5)
  );

  const handleFollowClick = (index) => {
    setFollowersCounts((prevCounts) => {
      const newCounts = [...prevCounts];
      newCounts[index] += 1;
      return newCounts;
    });
  };

  const externalImages = [
    "/img/elon.jpg", // Update the image path
    // Add more image links as needed
  ];

  return (
    <div className="home">
      {externalImages.map((image, index) => (
        <div key={index}>
          <h2>Followers: {followersCounts[index]}</h2>
          <img
            src={image}
            alt={`flowers ${index + 1}`}
            style={{ height: '5%', width: '5%' }}
          />
          <button onClick={() => handleFollowClick(index)}>Follow Me</button>
        </div>
      ))}
    </div>
  );
}
